package practiceagain;

public class Equals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String s1="Ashu";
		String s2="ashutosh";
		
		if (s1.equalsIgnoreCase(s2)) {
			System.out.println("They are equal");
		}
		else {
			System.out.println("They are not equal");
		}
	}

}
